mapper.update:
UPDATE
	mmc.product.participationItemPartial
SET
	isActive = #{isActive}
WHERE
	participationId = #{participationId}

mapper.select:
SELECT TOP 1 participationId, saleId, startDate, endDate, lastModifiedUserId, isActive, contentTypeId FROM mmc.product.participationItemPartial WHERE isActive = 0 AND (startDate <= #{processingDate} OR startDate IS NULL) AND (endDate > #{processingDate} OR endDate IS NULL)
AND participationId >= #{minParticipationId}
ORDER BY startDate

SELECT TOP 1 participationId, saleId, startDate, endDate, lastModifiedUserId, isActive, contentTypeId FROM mmc.product.participationItemPartial WHERE endDate IS NOT NULL AND endDate < #{processingDate}
AND participationId >= #{minParticipationId}
ORDER BY endDate

mapper.insert:
DECLARE @participationId INT = #{participationId}
DECLARE @uniqueidList VARCHAR(MAX) = #{csvUniqueIds}

INSERT INTO mmc.product.participationProduct (
	participationId, uniqueId, isOwner
)
SELECT
	@participationId as participationId,
	value as uniqueId,
	0
FROM
	string_split(@uniqueidList,',');
