mapper.select:
SELECT DISTINCT pricebook.Cost price, MPID.mpid mpn, manufacturer.name brand, status.status FROM mmc.product.product product INNER JOIN mmc.product.status status ON product.statusId = status.id INNER JOIN mmc.product.family family ON product.familyId = family.id INNER JOIN mmc.product.containerType containerType ON family.containerTypeId = containerType.id INNER JOIN mmc.product.finish finish ON product.finishId = finish.id INNER JOIN mmc.dbo.Manufacturer manufacturer ON family.manufacturerId = manufacturer.ID INNER JOIN mmc.product.feiMPID as MPID ON product.uniqueid = MPID.uniqueid INNER JOIN mmc.dbo.PriceBook_Cost_Log pricebookCostLog ON product.uniqueId = pricebookCostLog.UniqueId INNER JOIN mmc.dbo.PriceBook_Cost pricebook ON product.uniqueId = pricebook.UniqueId AND pricebookCostLog.PriceBookId = pricebook.PriceBookId INNER JOIN mmc.product.pricingFeiWhitelist pricingFeiWhitelist ON product.uniqueId = pricingFeiWhitelist.productUniqueId WHERE pricebook.PriceBookId = 1 AND status.status IN ('stock','nonstock')
AND pricebookCostLog.Update_DateTime > #{lastRanDate}
ORDER BY mpn
