mapper.select:
SELECT p.uniqueid product_id, f.id family_id, f.productId family_productId, m.id manufacturer_id, m.name manufacturer_name, fi.id finish_id, fi.finish finish_description, sd.freeShipping isFreeShipping, pbc.cost defaultPriceBookCost FROM mmc.dbo.Product_Most_Used_Vendor pmuv INNER JOIN mmc.product.product p ON p.uniqueId = pmuv.ProductUID INNER JOIN mmc.product.shippingDetail sd
ON sd.uniqueId = p.uniqueId AND (sd.LTL IS NULL OR sd.LTL = 0) INNER JOIN mmc.product.family f ON f.id = p.familyId INNER JOIN mmc.product.finish fi ON fi.id = p.finishId INNER JOIN mmc.dbo.Manufacturer m ON m.ID = f.manufacturerId INNER JOIN mmc.dbo.PriceBook_Cost pbc ON p.uniqueId = pbc.uniqueId AND pbc.priceBookId = 1 WHERE
p.statusId = ${@com.ferguson.cs.vendor.quickship.model.product.ProductStatus@STOCK.getIntValue()}
AND pmuv.VendorUID IN
#{id}
ORDER BY p.uniqueid ASC OFFSET #{criteria.offset} ROWS FETCH NEXT #{criteria.pageSize} ROWS ONLY
